var CIf;
CIf = (function(){
  CIf.displayName = 'CIf';
  var prototype = CIf.prototype, constructor = CIf;
  function CIf(cond, then, otherwise){
    this.cond = cond;
    this.then = then;
    this.otherwise = otherwise;
  }
  prototype.addElse = (function(){
    function addElse(elsenode){
      if (this.otherwise != null) {
        if (!(this.otherwise instanceof CIf)) {
          throw new Error("You already set an Else clause.");
        }
        return this.otherwise.addElse(elsenode);
      } else {
        return this.otherwise = elsenode;
      }
    }
    return addElse;
  }());
  return CIf;
}());
exports.CIf = CIf;